public boolean isOnline() {
        ConnectivityManager cm =
                (ConnectivityManager) getSystemService(Context.CONNECTIVITY_SERVICE);
        NetworkInfo netInfo = cm.getActiveNetworkInfo();
        return netInfo != null && netInfo.isConnectedOrConnecting();
    }

public class BoiledActivity extends AppCompatActivity implements View.OnClickListener {

        MediaPlayer mp;
        TextView ok;

        @java.lang.Override
        protected void onResume() {
            super.onResume();

            mp = new MediaPlayer();
            mp = MediaPlayer.create(this, R.raw.boiled);

            new java.lang.Thread() {
                public void run() {
                    mp.start();
                }
            }.start();
        }

        @java.lang.Override
        public void onCreate(Bundle savedInstanceState) {
            super.onCreate(savedInstanceState);
            setContentView(R.layout.activity_boiled);
            getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,
                    WindowManager.LayoutParams.FLAG_FULLSCREEN);
            getWindow().addFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON);

            KeyguardManager keyguardManager = (KeyguardManager) getSystemService(Context.KEYGUARD_SERVICE);
            KeyguardManager.KeyguardLock keyguardLock = keyguardManager.newKeyguardLock("TAG");
            keyguardLock.disableKeyguard();

            runOnUiThread(new java.lang.Runnable(){
                public void run(){
                    getWindow().addFlags(
                            WindowManager.LayoutParams.FLAG_SHOW_WHEN_LOCKED
                                    | WindowManager.LayoutParams.FLAG_TURN_SCREEN_ON
                                    | WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON
                                    | WindowManager.LayoutParams.FLAG_DISMISS_KEYGUARD);
                }
            });
            ok = (TextView) findViewById(R.id.tvStop);
            ok.setOnClickListener(this);
        }

        @java.lang.Override
        protected void onPause() {
            super.onPause();
            mp.stop();
        }

        @java.lang.Override
        public void onClick(View view) {
            onBackPressed();
        }
    }

private View.OnTouchListener onTouchListener = new View.OnTouchListener() {
        float startX;
        float startY;

        float tolerance = 100F;
        float dX, dY;

        @java.lang.Override
        public boolean onTouch(View view, MotionEvent event) {

            boolean doneX = false;
            boolean doneY = false;

            switch (event.getAction()) {

                case MotionEvent.ACTION_DOWN:
                    startX = view.getX();
                    startY = view.getY();
                    dX = view.getX() - event.getRawX();
                    dY = view.getY() - event.getRawY();
                    break;

                case MotionEvent.ACTION_MOVE:
                    view.setX(event.getRawX() + dX);
                    view.setY(event.getRawY() + dY);
                    break;

                case MotionEvent.ACTION_UP:

                    float x = view.getX() + view.getWidth() / 2;
                    float y = view.getY() + view.getHeight() / 2;


                    for (int o = 0; o < markers.size(); o++) {
                        ImageView marker = (ImageView) findViewById(markers.get(o));
                        float correctX = marker.getX() + marker.getWidth() / 2;
                        float correctY = marker.getY() + marker.getHeight() / 2;

                        java.lang.System.out.println(x + " " + correctX);
                        java.lang.System.out.println(y + " " + correctY);


                        if (x > correctX - tolerance && x < correctX + tolerance)
                            doneX = true;
                        if (y > correctY - tolerance && y < correctY + tolerance)
                            doneY = true;

                        if (doneX && doneY) {

                            view.setX(correctX - view.getWidth() / 2);
                            view.setY(correctY - view.getHeight() / 2);

                            charPresenter.playSound("correctly");
                            view.setOnTouchListener(null);
                            markers.remove(o);

                            if (markers.size() == 0) {
                                App.getProgressManagerInstance().saveProgress(getIntent().getStringExtra("name"));
                                charPresenter.onDone();
                            }
                            break;

                        } else if (o == markers.size() - 1) {
                            charPresenter.playSound("wrong");
                            view.setX(startX);
                            view.setY(startY);
                        }
                    }
                default:
                    return false;
            }
            return true;
        }
    };

disposable = Observable.interval(1, TimeUnit.MILLISECONDS)
            .compose(new SchedulersTransformer<>())
            .subscribe(aLong -> {
        if (aLong == 1)
            App.playSound("well_done");
        if (aLong == 1000)
            App.playSound("well_done_voice1");
        if (aLong == 2000)
            App.playSound("try_again");
        if (aLong == 3500)
            getViewState().restartActivity();
        if (App.isCancelled || aLong > 6000)
            disposable.dispose();
    });

