void init() {
        Retrofit retrofit = new Retrofit.Builder()
                .addCallAdapterFactory(RxJava2CallAdapterFactory.create())
                .addConverterFactory(GsonConverterFactory.create())
                .baseUrl("http://tomcat.kilograpp.com/songs/")
                .build();
        api = retrofit.create(TemplateJava.Api.class);
        songs = new ArrayList<>();
    }

    void request() {
        swipeRefreshLayout.setRefreshing(true);
        if (isOnline())
            api.getSongsList()
                    .subscribeOn(Schedulers.newThread())
                    .observeOn(AndroidSchedulers.mainThread())
                    .subscribe(response -> {
                        songs.clear();
                        for (TemplateJava.SongsResponse resp : response)
                            songs.add(resp.author + "\n\n" + resp.label);
                        swipeRefreshLayout.setRefreshing(false);
                    });
        else {
            Toast.makeText(getApplicationContext(), "Please check your connection", Toast.LENGTH_LONG).show();
            swipeRefreshLayout.setRefreshing(false);
        }
    }

    public interface Api {
        @GET(value"api/songs")
        Observable<List<TemplateJava.SongsResponse>> getSongsList();
    }

    public class SongsResponse {
        @SerializedName(value"label")
        @Expose
        public java.lang.String label = "";
        @SerializedName(value"author")
        @Expose
        public java.lang.String author = "";
        @SerializedName(value"id")
        @Expose
        public int id = 0;
    }

