public class AdActivity extends MvpAppCompatActivity implements AdView, BillingProcessor.IBillingHandler {

        @InjectPresenter
        AdPresenter adPresenter;

        private ActivityAdBinding binding;
        private BillingProcessor bp;

        @java.lang.Override
        protected void onResume() {
            super.onResume();
            adPresenter.checkTimer();
            App.loadRewardedVideoAd();
        }

        @java.lang.Override
        protected void onCreate(Bundle savedInstanceState) {
            super.onCreate(savedInstanceState);
            App.hideSystemUI(getWindow());
            binding = DataBindingUtil.setContentView(this, R.layout.activity_ad);
            bp = new BillingProcessor(this, "YOUR LICENSE KEY FROM GOOGLE PLAY CONSOLE HERE", this);
            adPresenter.checkTimer();
            binding.rlExit.setOnClickListener(v -> {
                App.playSound("neutral");
                startActivity(new Intent(getApplicationContext(), MainActivity.class));
                finish();
            });

            // просмотр ролика
            binding.rlAd1.setOnClickListener(v -> {
                App.playSound("neutral");
                App.showRewardedVideo(this);
            });

            // покупка приложения
            binding.rlAd2.setOnClickListener(v -> {
                App.playSound("neutral");
                if (BillingProcessor.isIabServiceAvailable(getApplicationContext()))
                    bp.purchase(this, "YOUR PRODUCT ID FROM GOOGLE PLAY CONSOLE HERE"); // // 
            });
        }

        public void checkTimer(java.lang.String timerText) {
            binding.tvTimer.setText(timerText);
        }

        @java.lang.Override
        public void onDestroy() {
            if (bp != null) {
                bp.release();
            }
            super.onDestroy();
        }

        // billing
        @java.lang.Override
        public void onProductPurchased(java.lang.String productId, TransactionDetails details) {
            AdManager.getInstance().setOffAdMillis();
            App.getProgressManagerInstance().saveMillis();
        }

        @java.lang.Override
        public void onPurchaseHistoryRestored() {

        }

        @java.lang.Override
        public void onBillingError(int errorCode, java.lang.Throwable error) {

        }

        @java.lang.Override
        public void onBillingInitialized() {

        }

        @java.lang.Override
        protected void onActivityResult(int requestCode, int resultCode, Intent data) {
            if (!bp.handleActivityResult(requestCode, resultCode, data)) {
                super.onActivityResult(requestCode, resultCode, data);
            }
        }

        @java.lang.Override
        public void onBackPressed() {
        }
    }

public class StreamPlayer {
        private final static java.lang.String stream = "http://stream1.radiostyle.ru:8001/radiovatan";
        private MediaPlayer mediaPlayer;
        private boolean ready = false;

        public CustomPlayer() {
            mediaPlayer = new MediaPlayer();
            mediaPlayer.setAudioStreamType(AudioManager.STREAM_MUSIC);
            try {
                mediaPlayer.setDataSource(stream);
            } catch (IOException e) {
                e.printStackTrace();
            }
            init();
        }

        public void init() {
            ready = false;
            mediaPlayer.prepareAsync();
            mediaPlayer.setOnPreparedListener(mp -> {
                mediaPlayer.start();
                ready = true;
            });
        }

        public void playStream() {
            mediaPlayer.start();
        }

        public void stopStream() {
            if (mediaPlayer.isPlaying()) {
                mediaPlayer.pause();
            }
        }

        public boolean isPlaying() {
            return mediaPlayer.isPlaying();
        }

        public boolean getReady() {
            return ready;
        }
    }

