interface APIregister {
   @POST("/api/register")
   @FormUrlEncoded
   fun postRegister(@Field("name") name: String,
                    @Field("surname") surname: String,
                    @Field("city") city: String,
                    @Field("password") password: String,
                    @Field("login") login: String,
                    @Field("email") email: String): Observable<RegisterResponse>
}

@Throws(java.io.IOException::class)
    fun exec(cmd: String): String {
        val s = Scanner(Runtime.getRuntime().exec(cmd).inputStream).useDelimiter("\\A")
        val target = StringBuilder()
        while (s.hasNext())
            target.append(s.next())
        return target.toString()
    }
