protected com.codeborne.selenide.SelenideElement safeClick(com.codeborne.selenide.SelenideElement element, int seconds) {
        sleep(1);
        return seconds == 0 ? element :
                (element.isDisplayed() ? click(element) :
                        safeClick(element, --seconds));
    }
